'),(1889,2153,'_menu_item_object','page'),(1890,2153,'_menu_item_target',''),(1891,2153,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1892,2153,'_menu_item_xfn',''),(1893,2153,'_menu_item_url',''),(1895,2154,'_menu_item_type','post_type'),(1896,2154,'_menu_item_menu_item_parent','0'),(1897,2154,'_menu_item_object_id','2147'),(1898,2154,'_menu_item_object','page'),(1899,2154,'_menu_item_target',''),(1900,2154,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1901,2154,'_menu_item_xfn',''),(1902,2154,'_menu_item_url',''),(1904,2155,'_menu_item_type','post_type'),(1905,2155,'_menu_item_menu_item_parent','0'),(1906,2155,'_menu_item_object_id','2145'),(1907,2155,'_menu_item_object','page'),(1908,2155,'_menu_item_target',''),(1909,2155,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1910,2155,'_menu_item_xfn',''),(1911,2155,'_menu_item_url',''),(1913,2156,'_menu_item_type','post_type'),(1914,2156,'_menu_item_menu_item_parent','0'),(1915,2156,'_menu_item_object_id','2143'),(1916,2156,'_menu_item_object','page'),(1917,2156,'_menu_item_target',''),(1918,2156,'_menu_item_classes','a:1:{i:0;s:0:\"\";}'),(1919,2156,'_menu_item_xfn',''),(1920,2156,'_menu_item_url','');
